const tabs=document.querySelectorAll(".tab"),dropdowns=document.querySelectorAll(".dropdown"),arrows=document.querySelectorAll(".arrow");tabs.forEach((s,e)=>{s.onclick=function(){dropdowns[e].classList.value.includes("open")?(dropdowns[e].classList.remove("open"),arrows[e].classList.remove("rotate")):(dropdowns.forEach(s=>{s.classList.remove("open")}),dropdowns[e].classList.add("open"),arrows.forEach(s=>{s.classList.remove("rotate")}),arrows[e].classList.add("rotate"))}});const sm_nav=document.querySelector(".sm-nav"),slide_in=document.querySelector(".nav");sm_nav.onclick=function(){slide_in.classList.value.includes("slide-in")?slide_in.classList.remove("slide-in"):slide_in.classList.add("slide-in")};